import React, { useState, useEffect } from "react";
import "./Chatbot.css";

const questionPool = [
  "가족과의 갈등을 어떻게 풀어야 할까?",
  "친구에게 상처를 받았을 때 어떻게 하면 좋을까?",
  "배신당한 기분에서 벗어나려면 어떻게 해야 할까?",
  "사람들에게 쉽게 마음을 열지 못하는데, 어떻게 하면 좋을까?",
  "연인과의 관계에서 신뢰가 깨졌을 때 어떻게 해야 할까?",
  "누군가를 용서하는 게 너무 어려운데, 방법이 있을까?",
  "내가 지금 가고 있는 길이 맞는 걸까?",
  "하고 싶은 일이 없는데, 어떻게 살아가야 할까?",
  "꿈이 없어서 고민이에요. 어떻게 찾을 수 있을까?",
  "무언가를 시작하는 게 두려워요. 어떻게 극복할 수 있을까?",
  "실패가 두려워서 도전하지 못하고 있어.",
  "나이는 계속 먹는데, 아직도 내 자리를 못 찾았어.",
  "다른 사람들과 비교하는 게 힘들어.",
  "내 감정을 컨트롤하는 게 어려워.",
  "늘 우울하고 무기력한데, 어떻게 해야 할까?",
  "자존감이 낮아서 힘들어.",
  "돈 걱정 없이 사는 방법이 있을까?",
  "노력해도 성과가 없을 때 어떻게 해야 할까?",
  "직장에서 인정받지 못하는 기분이야.",
  "퇴사하고 싶지만, 현실적으로 어려워.",
  "외로움을 극복하는 방법이 있을까?",
  "아무도 나를 이해해 주지 않는 것 같아.",
  "관계에서 오는 상처가 반복될 때 어떻게 해야 할까?",
  "다들 행복해 보이는데, 나만 그런 게 아닌 것 같아.",
  "혼자 있는 시간을 즐길 수 있는 방법이 있을까?",
];

const getRandomQuestions = (num) => {
  let shuffled = [...questionPool].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, num);
};

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [randomQuestions, setRandomQuestions] = useState([]);

  useEffect(() => {
    setRandomQuestions(getRandomQuestions(8));
  }, []);

  const sendMessage = (text) => {
    if (!text.trim()) return;
    setMessages([...messages, { sender: "user", text }]);
    setInput("");

    setTimeout(() => {
      setMessages((prevMessages) => [
        ...prevMessages,
        {
          sender: "bot",
          text: "응답을 가져올 수 없습니다. 다시 시도해 주세요.",
        },
      ]);
    }, 1000);
  };

  return (
    <div className="chat-container">
      <div className="chat-body">
        <div className="chat-message bot">
          안녕하세요 성도님! 무엇이든 편하게 물어보세요 😊
        </div>
        <div className="chat-question-buttons">
          {randomQuestions.map((question, index) => (
            <button key={index} onClick={() => sendMessage(question)}>
              {question}
            </button>
          ))}
        </div>

        {messages.map((msg, index) => (
          <div key={index} className={`chat-message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>

      <div className="chat-input-container">
        <input
          id="chat-input"
          type="text"
          placeholder="질문을 입력하세요..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && sendMessage(input)}
        />
        <button id="send-btn" onClick={() => sendMessage(input)}>
          ▶
        </button>
      </div>
    </div>
  );
};

export default Chatbot;
