// Chatbot.js
import React, { useState, useEffect, useRef } from "react";
import "./Chatbot.css";

const botProfile = process.env.PUBLIC_URL + "/bot-avatar.png";
const userProfile = process.env.PUBLIC_URL + "/user-avatar.png";

const categories = {
  relationships: {
    title: "인간관계",
    questions: [
      "가족과의 갈등을 어떻게 풀어야 할까요?",
      "친구관계에서 상처받았을 때는 어떻게 해야 할까요?",
      "대인관계가 어려울 때 어떻게 해야 할까요?",
      "용서하는 것이 어려울 때는 어떻게 해야 할까요?",
    ],
  },
  faith: {
    title: "신앙생활",
    questions: [
      "기도는 어떻게 하면 좋을까요?",
      "성경 읽기가 어려워요.",
      "예배생활이 게을러졌어요.",
      "하나님의 뜻을 어떻게 알 수 있을까요?",
    ],
  },
  life: {
    title: "일상과 결정",
    questions: [
      "중요한 결정을 앞두고 있어요.",
      "미래가 불안해요.",
      "습관을 바꾸고 싶어요.",
      "스트레스 관리가 필요해요.",
    ],
  },
};

const initialMessage = {
  id: "welcome",
  text: "안녕하세요! 성경과 삶에 대해 함께 이야기 나누어요. 어떤 주제에 관심이 있으신가요?",
  sender: "bot",
};

// 성경 구절을 파싱하는 함수
const parseScriptureVerses = (text) => {
  const verses = [];
  const pattern = /\((\d+)\)\s+([^-]+)-\s*"([^"]+)"/g;
  let match;

  while ((match = pattern.exec(text)) !== null) {
    verses.push({
      index: match[1],
      reference: match[2].trim(),
      content: match[3].trim(),
    });
  }

  // 원본 텍스트에서 성경 구절 부분을 제거
  const mainText = text.split("[추천 성경구절]")[0].trim();
  return { mainText, verses };
};

// 성경 구절 컴포넌트
const ScriptureVerse = ({ verse }) => (
  <div className="scripture-verse">
    <div className="scripture-header">
      <span className="scripture-number">{verse.index}</span>
      <span className="scripture-reference">{verse.reference}</span>
    </div>
    <div className="scripture-content">"{verse.content}"</div>
  </div>
);

const Chatbot = () => {
  // ... (기존 상태 및 함수들은 유지)

  const renderMessage = (message) => {
    const isBot = message.sender === "bot";

    if (isBot && message.text.includes("[추천 성경구절]")) {
      const { mainText, verses } = parseScriptureVerses(message.text);

      return (
        <div key={message.id} className="message-group">
          <div className={`message ${message.sender}`}>
            <div className="message-avatar">
              <img src={botProfile} alt="Bot Avatar" />
            </div>
            <div className="message-content">{mainText}</div>
          </div>

          {verses.length > 0 && (
            <div className="scripture-verses-container">
              {verses.map((verse, index) => (
                <div
                  key={index}
                  className={`message ${message.sender} scripture-message`}
                >
                  <div className="message-avatar">
                    <img src={botProfile} alt="Bot Avatar" />
                  </div>
                  <ScriptureVerse verse={verse} />
                </div>
              ))}
            </div>
          )}
        </div>
      );
    }

    return (
      <div key={message.id} className={`message ${message.sender}`}>
        <div className="message-avatar">
          <img
            src={isBot ? botProfile : userProfile}
            alt={`${isBot ? "Bot" : "User"} Avatar`}
          />
        </div>
        <div className="message-content">{message.text}</div>
      </div>
    );
  };

  // ... (나머지 컴포넌트 코드는 유지)

  return (
    <div className="chat-container">
      <div className="chat-header">
        <div className="header-profile">
          <img src={botProfile} alt="Bot Profile" />
        </div>
        <div className="header-text">
          <h1>성경 챗봇</h1>
          <p>성경과 삶의 이야기를 나눠요</p>
        </div>
      </div>

      <div className="chat-messages">
        {messages.map(renderMessage)}
        {renderSuggestions()}
        {isLoading && (
          <div className="loading-indicator">
            <span></span>
            <span style={{ animationDelay: "0.2s" }}></span>
            <span style={{ animationDelay: "0.4s" }}></span>
          </div>
        )}
        <div ref={chatEndRef} />
      </div>

      <div className="chat-input-container">
        <input
          type="text"
          className="chat-input"
          placeholder="메시지를 입력하세요..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              e.preventDefault();
              handleSend(input);
            }
          }}
        />
        <button
          className="send-button"
          onClick={() => handleSend(input)}
          disabled={isLoading || !input.trim()}
        >
          <svg
            width="20"
            height="20"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <path d="M22 2L11 13M22 2l-7 20-4-9-9-4 20-7z" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default Chatbot;
