// Chatbot.js
import React, { useState, useEffect, useRef } from "react";
import "./Chatbot.css";

const botProfile = process.env.PUBLIC_URL + "/bot-avatar.png";
const userProfile = process.env.PUBLIC_URL + "/user-avatar.png";

const categories = {
  relationships: {
    title: "Ïù∏Í∞ÑÍ¥ÄÍ≥Ñ",
    questions: [
      "Í∞ÄÏ°±Í≥ºÏùò Í∞àÎì±ÏùÑ Ïñ¥ÎñªÍ≤å ÌíÄÏñ¥Ïïº Ìï†ÍπåÏöî?",
      "ÏπúÍµ¨Í¥ÄÍ≥ÑÏóêÏÑú ÏÉÅÏ≤òÎ∞õÏïòÏùÑ ÎïåÎäî Ïñ¥ÎñªÍ≤å Ìï¥Ïïº Ìï†ÍπåÏöî?",
      "ÎåÄÏù∏Í¥ÄÍ≥ÑÍ∞Ä Ïñ¥Î†§Ïö∏ Îïå Ïñ¥ÎñªÍ≤å Ìï¥Ïïº Ìï†ÍπåÏöî?",
      "Ïö©ÏÑúÌïòÎäî Í≤ÉÏù¥ Ïñ¥Î†§Ïö∏ ÎïåÎäî Ïñ¥ÎñªÍ≤å Ìï¥Ïïº Ìï†ÍπåÏöî?",
    ],
  },
  faith: {
    title: "Ïã†ÏïôÏÉùÌôú",
    questions: [
      "Í∏∞ÎèÑÎäî Ïñ¥ÎñªÍ≤å ÌïòÎ©¥ Ï¢ãÏùÑÍπåÏöî?",
      "ÏÑ±Í≤Ω ÏùΩÍ∏∞Í∞Ä Ïñ¥Î†§ÏõåÏöî.",
      "ÏòàÎ∞∞ÏÉùÌôúÏù¥ Í≤åÏùÑÎü¨Ï°åÏñ¥Ïöî.",
      "ÌïòÎÇòÎãòÏùò ÎúªÏùÑ Ïñ¥ÎñªÍ≤å Ïïå Ïàò ÏûàÏùÑÍπåÏöî?",
    ],
  },
  life: {
    title: "ÏùºÏÉÅÍ≥º Í≤∞Ï†ï",
    questions: [
      "Ï§ëÏöîÌïú Í≤∞Ï†ïÏùÑ ÏïûÎëêÍ≥† ÏûàÏñ¥Ïöî.",
      "ÎØ∏ÎûòÍ∞Ä Î∂àÏïàÌï¥Ïöî.",
      "ÏäµÍ¥ÄÏùÑ Î∞îÍæ∏Í≥† Ïã∂Ïñ¥Ïöî.",
      "Ïä§Ìä∏Î†àÏä§ Í¥ÄÎ¶¨Í∞Ä ÌïÑÏöîÌï¥Ïöî.",
    ],
  },
};

const initialMessage = {
  id: "welcome",
  text: "ÏïàÎÖïÌïòÏÑ∏Ïöî! ÏÑ±Í≤ΩÍ≥º ÏÇ∂Ïóê ÎåÄÌï¥ Ìï®Íªò Ïù¥ÏïºÍ∏∞ ÎÇòÎàÑÏñ¥Ïöî. Ïñ¥Îñ§ Ï£ºÏ†úÏóê Í¥ÄÏã¨Ïù¥ ÏûàÏúºÏã†Í∞ÄÏöî?",
  sender: "bot",
};

const Chatbot = () => {
  const [messages, setMessages] = useState([
    {
      id: "welcome",
      text: "ÏïàÎÖïÌïòÏÑ∏Ïöî! ÏÑ±Í≤ΩÍ≥º ÏÇ∂Ïóê ÎåÄÌï¥ Ïù¥ÏïºÍ∏∞ ÎÇòÎàÑÏñ¥Ïöî. Î¨¥ÏóáÏù¥Îì† Î¨ºÏñ¥Î≥¥ÏÑ∏Ïöî!",
      sender: "bot",
    },
  ]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  const formatResponse = async (text) => {
    try {
      const response = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({ message: text }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      }

      const data = await response.json();
      return data.message;
    } catch (error) {
      console.error("Error:", error);
      throw error;
    }
  };

  const handleSend = async (text) => {
    if (!text.trim()) return;

    const userMessage = { id: Date.now(), text, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");

    // üöÄ ÏÑúÎ≤Ñ ÏöîÏ≤≠ (Ïã§Ï†ú API Ïó∞Îèô)
    try {
      const response = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message: text }),
      });

      const data = await response.json();
      const botMessage = {
        id: Date.now() + 1,
        text: data.message,
        sender: "bot",
      };

      setMessages((prev) => [...prev, botMessage]);

      // ‚úÖ ÏÑ±Í≤Ω Íµ¨Ï†àÏùÑ Í∞úÎ≥Ñ Î©îÏãúÏßÄÎ°ú Î∂ÑÎ¶¨ÌïòÏó¨ Ï∂îÍ∞Ä
      if (data.bibleVerses) {
        data.bibleVerses.forEach((verse, index) => {
          setMessages((prev) => [
            ...prev,
            {
              id: Date.now() + index + 2,
              text: verse,
              sender: "bot",
              isBibleVerse: true,
            },
          ]);
        });
      }
    } catch (error) {
      console.error("Error:", error);
      const errorMessage = {
        id: Date.now() + 1,
        text: "ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥ Ï£ºÏÑ∏Ïöî.",
        sender: "bot",
      };
      setMessages((prev) => [...prev, errorMessage]);
    }
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    const message = {
      id: Date.now(),
      text: `${categories[category].title}Ïóê ÎåÄÌï¥ Ïù¥ÏïºÍ∏∞ ÎÇòÎàÑÏñ¥ Î≥¥ÏïÑÏöî.`,
      sender: "bot",
    };
    setMessages((prev) => [...prev, message]);
  };

  const renderMessage = (message) => {
    const isBot = message.sender === "bot";
    return (
      <div key={message.id} className={`message ${message.sender}`}>
        <div className="message-avatar">
          <img
            src={isBot ? botProfile : userProfile}
            alt={`${isBot ? "Bot" : "User"} Avatar`}
          />
        </div>
        <div className="message-content">{message.text}</div>
      </div>
    );
  };

  const renderSuggestions = () => {
    if (selectedCategory) {
      return (
        <div className="question-suggestions">
          {categories[selectedCategory].questions.map((question, index) => (
            <button
              key={index}
              className="suggestion-button"
              onClick={() => handleSend(question)}
            >
              {question}
            </button>
          ))}
        </div>
      );
    }

    return (
      <div className="question-suggestions">
        {Object.entries(categories).map(([key, category]) => (
          <button
            key={key}
            className="suggestion-button"
            onClick={() => handleCategorySelect(key)}
          >
            {category.title}
          </button>
        ))}
      </div>
    );
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <div className="header-profile">
          <img src={botProfile} alt="Bot Profile" />
        </div>
        <div className="header-text">
          <h1>ÏÑ±Í≤Ω Ï±óÎ¥á</h1>
          <p>ÏÑ±Í≤ΩÍ≥º ÏÇ∂Ïùò Ïù¥ÏïºÍ∏∞Î•º ÎÇòÎà†Ïöî</p>
        </div>
      </div>

      <div className="chat-messages">
        {messages.map(renderMessage)}
        {renderSuggestions()}
        {isLoading && (
          <div className="loading-indicator">
            <span></span>
            <span style={{ animationDelay: "0.2s" }}></span>
            <span style={{ animationDelay: "0.4s" }}></span>
          </div>
        )}
        <div ref={chatEndRef} />
      </div>

      <div className="chat-input-container">
        <input
          type="text"
          className="chat-input"
          placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              e.preventDefault();
              handleSend(input);
            }
          }}
        />
        <button
          className="send-button"
          onClick={() => handleSend(input)}
          disabled={isLoading || !input.trim()}
        >
          <svg
            width="20"
            height="20"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <path d="M22 2L11 13M22 2l-7 20-4-9-9-4 20-7z" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default Chatbot;
